mapper.insert:

	select SEQ_orders.nextval from dual

insert into
orders(
ID,

	,trade_id
	,num
	,product_id
	,product_name
	,product_real_price
	,product_pic_path
	,total_price
	,total_real_price
	,total_discount_price
	,GMT_CREATE
	,GMT_MODIFY
	,status
	,refund_status
	,sku_id
	,properties
	,propertiesName

)values(
#{id},

	,#{tradeId,jdbcType=BIGINT}
	,#{num,jdbcType=INTEGER}
	,#{productId,jdbcType=BIGINT}
	,#{productName,jdbcType=VARCHAR}
	,#{productRealPrice,jdbcType=NUMERIC}
	,#{productPicPath,jdbcType=VARCHAR}
	,#{totalPrice,jdbcType=NUMERIC}
	,#{totalRealPrice,jdbcType=NUMERIC}
	,#{totalDiscountPrice,jdbcType=NUMERIC}
	,
	
	,
	
	,#{status,jdbcType=INTEGER}
	,#{refundStatus,jdbcType=INTEGER}
	,#{skuId,jdbcType=BIGINT}
	,#{properties,jdbcType=VARCHAR}
	,#{propertiesname,jdbcType=VARCHAR}

)

mapper.sql:
FROM orders o

	
		o.id in
		#{item}
	
	and o.trade_id = #{tradeId}
	and o.num = #{num}
	and o.product_id = #{productId}
	and o.product_name = #{productName}
	and o.product_real_price >= #{productRealPriceFrom}
	and o.product_real_price <= #{productRealPriceTo}
	and o.product_pic_path = #{productPicPath}
	and o.total_price >= #{totalPriceFrom}
	and o.total_price <= #{totalPriceTo}
	and o.total_real_price >= #{totalRealPriceFrom}
	and o.total_real_price <= #{totalRealPriceTo}
	and o.total_discount_price >= #{totalDiscountPriceFrom}
	and o.total_discount_price <= #{totalDiscountPriceTo}
	and o.GMT_CREATE >= #{gmtCreateFrom}
	
		
			and o.GMT_CREATE < #{gmtCreateTo} + 1
			and o.GMT_CREATE < date_add(#{gmtCreateTo}, interval 1 day)
		
	
	and o.GMT_MODIFY >= #{gmtModifyFrom}
	
		
			and o.GMT_MODIFY < #{gmtModifyTo} + 1
			and o.GMT_MODIFY < date_add(#{gmtModifyTo}, interval 1 day)
		
	
	
		and o.status = #{status}
		
			and o.status in
			#{item}
			
		
		
			and o.status > 0
		
	
	and o.refund_status = #{refundStatus}
	and o.sku_id = #{skuId}
	and o.properties = #{properties}
	and o.propertiesName = #{propertiesname}

mapper.update:
update orders 

	trade_id=#{tradeId,jdbcType=BIGINT},
	num=#{num,jdbcType=INTEGER},
	product_id=#{productId,jdbcType=BIGINT},
	product_name=#{productName,jdbcType=VARCHAR},
	product_real_price=#{productRealPrice,jdbcType=NUMERIC},
	product_pic_path=#{productPicPath,jdbcType=VARCHAR},
	total_price=#{totalPrice,jdbcType=NUMERIC},
	total_real_price=#{totalRealPrice,jdbcType=NUMERIC},
	total_discount_price=#{totalDiscountPrice,jdbcType=NUMERIC},
	GMT_CREATE=#{gmtCreate,jdbcType=TIMESTAMP},
	GMT_MODIFY=
	
	,
	status=#{status,jdbcType=INTEGER},
	refund_status=#{refundStatus,jdbcType=INTEGER},
	sku_id=#{skuId,jdbcType=BIGINT},
	properties=#{properties,jdbcType=VARCHAR},
	propertiesName=#{propertiesname,jdbcType=VARCHAR},


	
		id in
		#{item}
	
	and trade_id = #{tradeId}
	and num = #{num}
	and product_id = #{productId}
	and product_name = #{productName}
	and product_real_price >= #{productRealPriceFrom}
	and product_real_price <= #{productRealPriceTo}
	and product_pic_path = #{productPicPath}
	and total_price >= #{totalPriceFrom}
	and total_price <= #{totalPriceTo}
	and total_real_price >= #{totalRealPriceFrom}
	and total_real_price <= #{totalRealPriceTo}
	and total_discount_price >= #{totalDiscountPriceFrom}
	and total_discount_price <= #{totalDiscountPriceTo}
	and GMT_CREATE >= #{gmtCreateFrom}
	
		
			and GMT_CREATE < #{gmtCreateTo} + 1
			and GMT_CREATE < date_add(#{gmtCreateTo}, interval 1 day)
		
	
	and GMT_MODIFY >= #{gmtModifyFrom}
	
		
			and GMT_MODIFY < #{gmtModifyTo} + 1
			and GMT_MODIFY < date_add(#{gmtModifyTo}, interval 1 day)
		
	
	
		and status = #{status}
		
			and status in
			#{item}
			
		
		
			and status > 0
		
	
	and refund_status = #{refundStatus}
	and sku_id = #{skuId}
	and properties = #{properties}
	and propertiesName = #{propertiesname}
